mapper.select:
select 
`article`.`id`,
`article`.`groupId`,
`article`.`uid`,
`article`.`status`,
`article`.`detail`,
`article`.`desc`,
`article`.`title`,
`article`.`deletedAt`,
`article`.`updatedAt`,
`article`.`createdAt`,
`user`.`readMe` as userModelReadMe,
`user`.`name` as userModelName,
`user`.`deletedAt` as userModelDeletedAt,
`user`.`updatedAt` as userModelUpdatedAt,
`user`.`createdAt` as userModelCreatedAt,
`articleGroup`.`uid` as articleGroupModelUid,
`articleGroup`.`desc` as articleGroupModelDesc,
`articleGroup`.`groupName` as articleGroupModelGroupName,
`articleGroup`.`deletedAt` as articleGroupModelDeletedAt,
`articleGroup`.`updatedAt` as articleGroupModelUpdatedAt,
`articleGroup`.`createdAt` as articleGroupModelCreatedAt,
`articleTagList`.`articleId` as articleTagListModelArticleId,
`articleTagList`.`tagName` as articleTagListModelTagName,
`articleTagList`.`deletedAt` as articleTagListModelDeletedAt,
`articleTagList`.`updatedAt` as articleTagListModelUpdatedAt,
`articleTagList`.`createdAt` as articleTagListModelCreatedAt
from `article`
left join `user` as `user` on `user`.`id` = `article`.`uid`
left join `articleGroup` as `articleGroup` on `articleGroup`.`id` = `article`.`groupId`
left join `articleTag` as `articleTagList` on `articleTagList`.`articleId` = `article`.`id`

      and `article`.`id` = #{id}
