mapper.select:
select 
`article`.`id`,
`article`.`createdAt`,
`article`.`updatedAt`,
`article`.`deletedAt`,
`article`.`title`,
`article`.`desc`,
`article`.`detail`,
`article`.`status`,
`article`.`uid`,
`article`.`groupId`,
`userModel`.`id` as userModelId,
`userModel`.`createdAt` as userModelCreatedAt,
`userModel`.`updatedAt` as userModelUpdatedAt,
`userModel`.`deletedAt` as userModelDeletedAt,
`userModel`.`name` as userModelName,
`userModel`.`readMe` as userModelReadMe,
`articleGroupModel`.`id` as articleGroupModelId,
`articleGroupModel`.`createdAt` as articleGroupModelCreatedAt,
`articleGroupModel`.`updatedAt` as articleGroupModelUpdatedAt,
`articleGroupModel`.`deletedAt` as articleGroupModelDeletedAt,
`articleGroupModel`.`groupName` as articleGroupModelGroupName,
`articleGroupModel`.`desc` as articleGroupModelDesc,
`articleGroupModel`.`uid` as articleGroupModelUid,
`articleTag`.`id` as articleTagListModelId,
`articleTag`.`createdAt` as articleTagListModelCreatedAt,
`articleTag`.`updatedAt` as articleTagListModelUpdatedAt,
`articleTag`.`deletedAt` as articleTagListModelDeletedAt,
`articleTag`.`tagName` as articleTagListModelTagName,
`articleTag`.`articleId` as articleTagListModelArticleId
from article
left join user as userModel on userModel.id = article.uid
left join articleGroup as articleGroupModel on articleGroupModel.id = article.groupId
left join articleTag on articleTag.articleId = article.id


      and `article`.`id` = #{id}
      and `article`.`id` < #{idLess}
